mapper.update:
update TimeClock
set timeOut = #{timeOut}, status = #{status}
where id = #{id} and clockDate = #{clockDate} and timeOut is null

mapper.select:
select t.* 
  from timeClock t 
 inner join employee e on e.id = t.id
 where t.clockDate = #{date} and e.managerId = #{managerId}
 and id = #{id}
 and status = #{status}
